// 1. Get the string from the "text" field
const rawText = $input.first().json.text;

try {
  // 2. Parse the string into a real JavaScript array
  const parsedArray = JSON.parse(rawText);

  // 3. Map the array to n8n's required structure: [{ json: { ... } }, { json: { ... } }]
  return parsedArray.map(item => {
    return {
      json: item
    };
  });
} catch (error) {
  // Fallback in case the AI returns invalid JSON
  throw new Error("Failed to parse AI output. Check the AI node response format.");
}
